class Solution:
    def countingsort(self, lst, p, d=10):
        counts = [0]*d
        for sr in lst:
            key = (sr[0] // p) % 10
            counts[key] += 1
            
        # starting indeices
        start = 0
        for i, c in enumerate(counts):
            counts[i] = start
            start += c
        
        # init new list for sort elements and elements' index
        elist = [0]*len(lst)  
        for n in lst:
            key = (n[0] // p) % 10
            elist[counts[key]] = n
            counts[key] += 1
            
        return elist
    
    def radixsort(self, nums, trim, k):
        # copy list
        new = [ [int(n[-trim:]),i] for i,n in enumerate(nums)]
        
        # digit place for sort, p is place_value
        for t in range(0, trim, 1):
            p = 10**t
            new = self.countingsort(new, p)
            
        return new[k-1][1]
        
    def smallestTrimmedNumbers(self, nums: List[str], queries: List[List[int]]) -> List[int]:
        res = []
        while queries:
            k, trim = queries.pop(0)                       
            res.append(self.radixsort(nums, trim, k))            
        return res
       
        